import { useEffect, useState } from "react"
import { Container, Spinner, Table } from "react-bootstrap"
import NavBar from "../components/NavBar"
import { GetPersonal } from "../utility/APIConn"

const Vista = () => {

    const [data, setData] = useState(undefined)
    const [itemSeleccionado, setitemSeleccionado] = useState(undefined)
    const SeleccionarUsuario = (item) => {
        setitemSeleccionado(item.data());
        OpenCLoseModalVer()
    }

    const [modalVer, setModalVer] = useState(false)
    const OpenCLoseModalVer = () => {
        setModalVer(!modalVer)
    }

    useEffect(()=>{
        GetPersonal(setData)
    },[])

    return (
        <>
            <NavBar />
            {data !== undefined ?

                <Container className="my-5">
                    <Table bordered striped hover responsive variant="dark">
                        <thead>
                            <tr className="align-middle">
                                <th>ID</th>
                                <th>Nombre</th>
                                <th>Opciones</th>

                            </tr>
                        </thead>
                        <tbody className="align-middle">
                            {data.map((item, idx) => (
                                <tr key={idx}>
                                    <td>{item.id}</td>
                                    <td>{item.nombre}</td>
                                    <td>
                                        <button className="btn btn-primary m-1" onClick={() => SeleccionarUsuario(item)}>Ver</button>
                                    </td>
                                </tr>
                            ))}

                        </tbody>
                    </Table>
                </Container>
                :
                <>
                    <div className="text-center mt-4">
                        <Spinner animation="grow" variant="danger" />
                    </div>
                </>
            }
        </>
    )
}

export default Vista
